(function () {
  'use strict';

  angular
    .module('lucidworksView', [
      'ui.router',
      'ngAnimate',
      'ngSanitize',

      // Foundation
      'foundation',
      'foundation.dynamicRouting',
      'foundation.dynamicRouting.animations',

      // Libraries
      'ngOrwell',
      'rison',

      // Fusion Seed App
      'lucidworksView.components',
      'lucidworksView.services',
      'lucidworksView.controllers',

      //Datepicker
      'ADM-dateTimePicker',

      'checklist-model'
    ])
    .constant('_', window._) //eslint-disable-line
    .config(config)
    .run(run);

  /**
   * Main app config
   *
   * @param  {Provider} $logProvider          Provider for log
   * @param  {Provider} $urlRouterProvider    Provider for url
   * @param  {Provider} $httpProvider         Provider for http
   * @param  {Provider} $locationProvider     Provider for location
   * @param  {Provider} ApiBaseProvider       Provider for ApiBase
   * @param  {Provider} ConfigServiceProvider Provider for ConfigService
   */
  function config($logProvider, $urlRouterProvider, $httpProvider, $locationProvider, ApiBaseProvider,
    ConfigServiceProvider, $windowProvider) {
    'ngInject';

    $logProvider.debugEnabled(true);

    $urlRouterProvider.otherwise('search');

/*
    $locationProvider.html5Mode({
      enabled: true,
      requireBase: false
    });
    $locationProvider.hashPrefix('!');
*/

    // If using a proxy use the same url.
    $httpProvider.interceptors.push('AuthInterceptor');

    if (ConfigServiceProvider.config.use_proxy) {
      var $window = $windowProvider.$get();
      ApiBaseProvider.setEndpoint($window.location.protocol + '//' + $window.location.host + '/');
      $httpProvider.defaults['withCredentials'] = true;

    } else {
      ApiBaseProvider.setEndpoint(ConfigServiceProvider.getFusionUrl());
    }
  }

  /**
   * Main app run time
   *
   * @param  {Service} $document     Document service
   */
  function run($document, $rootScope, ConfigService) {
    'ngInject';
    $rootScope.title = ConfigService.config.search_app_title;
  }
})();
(function() {
  'use strict';

  angular
    .module('lucidworksView.services.apiBase', [])
    .provider('ApiBase', ApiBase);


  function ApiBase() {
    'ngInject';
    var self = this;
    self.endpoint = '';
    self.$get = get;
    self.setEndpoint = setEndpoint;
    self.getEndpoint = getEndpoint;

    function setEndpoint(endpoint) {
      self.endpoint = endpoint;
    }

    function getEndpoint() {
      return self.endpoint;
    }

    function get() {
      return {
        getEndpoint: getEndpoint,
        setEndpoint: setEndpoint
      };
    }
  }
})();
(function() {
  'use strict';

  angular
    .module('lucidworksView.services.authInterceptor', ['lucidworksView.services.config'])
    .factory('AuthInterceptor', AuthInterceptor);


  function AuthInterceptor($q, $log, $rootScope, $injector) {
    'ngInject';
    var tryingAnon = false;
    return {
      responseError: responseError
    };

    function responseError(resp) {
      var deferred = $q.defer();
      var $state = $injector.get('$state');
      // CASE: If the app is on login page, the code is 401 and the request wasn't a api/session POST, then only attempt anon login
      // The reason for this check so that not all the response errors are intercepted
      if (!$state.is('login') && (resp.status === 401) && !isLoginRequest(resp.config)) {
        //CASE: If not already trying anonymous session then do try...
        if(!tryingAnon) {
          //CASE: If there are usable anon creds, then try it out
          if(useAnonCreds()){
            $log.info('Creating anonymous session with credentials from FUSION_CONFIG.js');
            getAnonSession()
              .then(function() {
                //CASE: If anonymous session creation is successful, go home
                $log.info('Created anonymous session');
                deferred.reject();
                $state.go('rules',{query:'(q:\'*\')'}); // TODO we should go back we we come from
              }, function(err) {
                if (err.status === 201) {
                  // TODO: Investigate why 201 is going to error handler...
                  // If it's the expected behaviour, figure out a better solution
                  //CASE: If anonymous login succeeded with a 201 response, go to `home`

                  $log.info('Created anonymous session');
                  $state.go('rules', {query: '(q:\'*\')'}); // TODO we should go back we we come from
                } else {
                  //CASE: If anonymous login failed, then go to login
                  $log.info('Failed to create anonymous session');
                  $state.go('login');
                }
                deferred.reject(err);
              });
          } else{
            //CASE: If anonymous login creds are unusable then go to login
            deferred.reject();
            $state.go('login');
          }
        } else{
          //CASE: If trying anon login, then that promise chain will take care of stuff
          deferred.reject();
        }
      } else if(resp.status === 403){
        //CASE: If unauthorized, don't bother.
        // TODO handle unauthorized users.
        $log.warn('You are unauthorized to access that endpoint');
        deferred.reject(false);
      } else{
        deferred.reject(resp);
      }
      // In all cases reject the promise chain

      return deferred.promise;
    }

    //////////////
    ///

    /**
     * [getAnonSession Creates anonymous session with the given config username:passwoed]
     * @return {promise} [Promise for creating the session]
     */
    function getAnonSession(){
      var ConfigService = $injector.get('ConfigService'),
        AuthService = $injector.get('AuthService'),
        $q = $injector.get('$q');

      tryingAnon = true;
      var def = $q.defer();
      AuthService.createSession(ConfigService.config.anonymous_access.username, ConfigService.config.anonymous_access.password)
        .then(function(resp){
          tryingAnon = false;
          def.reject(resp);
        }).catch(function(error){
          tryingAnon = false;
          def.reject(error);
        });
      return def.promise;
    }

    /**
     * [useAnonCreds Checks if it's okay to use anonymous credentials]
     * @return {Boolean} [Whether it's okay or not]
     */
    function useAnonCreds(){
      var ConfigService = $injector.get('ConfigService'),
        anonAccess = ConfigService.config.anonymous_access;

      return !(anonAccess.username === '' || anonAccess.password === '');
    }

    /**
     * [isLoginRequest Checks if a given resp.config originated from a /api/session POST]
     * @param  {Object}  respConfig [The resp.config object]
     * @return {Boolean}            [Whether login request or not]
     */
    function isLoginRequest(respConfig){
      //TODO: Make this better
      var ApiBase = $injector.get('ApiBase');
      return (respConfig.url.indexOf(ApiBase.getEndpoint() + 'api/session') !== -1) && (respConfig.method === 'POST');
    }

  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.services.auth', ['lucidworksView.services.apiBase',
      'lucidworksView.services.config'
    ])
    .factory('AuthService', AuthService);

  function AuthService($q, $log, $http, $rootScope, ApiBase, ConfigService) {
    'ngInject';
    var config = ConfigService.config;
    var realmName = config.connection_realm;

    return {
      createSession: createSession,
      getSession: getSession,
      destroySession: destroySession
    };

    //////////////


    function createSession(username, password) {
      var deferred = $q.defer();
      $http
        .post(ApiBase.getEndpoint() + 'api/session?realmName=' + realmName, {
          username: username,
          password: password
        })
        .then(function (resp) {
          deferred.resolve(resp);
        }, function (err) {
          deferred.reject(err);
        });

      return deferred.promise;
    }

    function getSession() {
      var deferred = $q.defer();
      $http
        .get(ApiBase.getEndpoint() + 'api/session?realmName=' + realmName)
        .then(function (resp) {
          deferred.resolve(resp);
        }, function (err) {
          deferred.reject(err);
        });
      return deferred.promise;
    }

    function destroySession() {
      var deferred = $q.defer();
      $http
        .delete(ApiBase.getEndpoint() + 'api/session?realmName=' + realmName)
        .then(function (resp) {
          deferred.resolve(resp);
        });

      return deferred.promise;
    }
  }
})();
(function () {
  'use strict';
  angular
    .module('lucidworksView.services.config', [])

    /** Default config options **/
    .constant('CONFIG_DEFAULT', {
      host: 'http://localhost',
      port: '8764',
      connection_realm: 'native',
      anonymous_access: {
        username: '',
        password: ''
      },
      // change default for fusion 2.3
      use_proxy: true,
      collection: 'MyCollection',
      logo_location: 'assets/img/logo/lucidworks-white.svg',
      query_debug: false,
      query_pipeline_id: 'default',
      query_profile_id: 'default',
      use_query_profile: true,
      search_app_title: 'Fusion Seed App',
      head_field: 'title',
      subhead_field: 'subtitle',
      description_field: 'description',
      head_url_field: 'url',
      image_field: 'image',
      fields_to_display: [],
      field_display_labels: {},
      sort_fields: [],
      signal_type: 'click',
      signals_pipeline: '_signals_ingest',
      signals_document_id: 'id',
      facets: [],
      docs_per_page: 10,
      typeahead_use_query_profile: true,
      typeahead_query_pipeline_id: 'default',
      typeahead_query_profile_id: 'default',
      typeahead_fields: ['id'],
      typeahead_requesthandler: 'select',
      landing_page_redirect: true
    })
    /** Config overrides from FUSION_CONFIG.js **/
    .constant('CONFIG_OVERRIDE', window.appConfig) //eslint-disable-line

    .provider('ConfigService', ConfigService);


  /**
   * ConfigService
   *
   * @param {object} CONFIG_DEFAULT  [description]
   * @param {object} CONFIG_OVERRIDE [description]
   */
  function ConfigService(CONFIG_DEFAULT, CONFIG_OVERRIDE) {
    'ngInject';
    var appConfig,
      vm = this;

    vm.$get = $get;
    vm.getFusionUrl = getFusionUrl;

    /* initialize on first load */
    init();

    /////////////

    /**
     * Main Service function.
     */
    function $get() {
      'ngInject';

      return {
        init: init, //TODO: Only for test env
        config: appConfig,
        getFusionUrl: getFusionUrl,
        getQueryProfile: getQueryProfile,
        getCollectionName: getCollectionName,
        getQueryPipeline: getQueryPipeline,
        getLoginCredentials: getLoginCredentials,
        getIfQueryProfile: getIfQueryProfile,
        getFieldLabels: getFieldLabels,
        getFieldsToDisplay: getFieldsToDisplay,
        getTypeaheadConfig: getTypeaheadConfig,
        getTypeaheadRequestHandler: getTypeaheadRequestHandler,
        getTypeaheadField: getTypeaheadField,
        getTypeaheadProfile: getTypeaheadProfile,
        getTypeaheadPipeline: getTypeaheadPipeline,
        getLandingPageRedirect: getLandingPageRedirect,
        getFields: {
          all: getAllFields,
          get: getSpecificField
        }
      };
    }

    /**
     * Extend config with the defaults
     */
    function init() {
      // Set local override based on arguements passed in.
      var localOverride = (arguments.length > 0) ? arguments[0] : {};

      appConfig = _.assign({}, CONFIG_DEFAULT, CONFIG_OVERRIDE, localOverride);
      vm.config = appConfig;
    }

    function getIfQueryProfile() {
      return appConfig.use_query_profile;
    }

    /**
     * Returns a fusion URL complete w/ endslash.
     */
    function getFusionUrl() {
      return appConfig.host + ':' + appConfig.port + '/';
    }

    function getQueryPipeline() {
      return appConfig.query_pipeline_id;
    }

    function getQueryProfile() {
      return appConfig.query_profile_id;
    }

    function getLoginCredentials() {
      return {
        username: appConfig.user,
        passowrd: appConfig.password
      };
    }

    function getLandingPageRedirect(){
      return appConfig.landing_page_redirect;
    }

    function getCollectionName() {
      return appConfig.collection;
    }

    /**
     * @return {object}
     *
     * Returns all the config properties that
     * ends with a `_field` which is not a blank string
     * and is toggled by explicit enable-ment by `_enabled` of the same type
     */
    function getAllFields() {
      var fieldsMap = {};
      _.keys(appConfig)
        .filter(function (item) {
          return item.match(/\_field$/);
        })
        .filter(function (item) {
          return _.trim(appConfig[item]) !== '';
        })
        .forEach(function (keyName) {
          fieldsMap[keyName] = appConfig[keyName];
        });
      return fieldsMap;
    }

    /**
     * Returns specific field of given type.
     * @param  {string} fieldType The type of field that needs to be fetch from the config
     * @return {*|null}           The value of the said field or null if not found.
     */
    function getSpecificField(fieldType) {
      var allFields = getAllFields();
      if (!fieldType.match(/\_field$/)) {
        fieldType += '_field';
      }
      return allFields[fieldType] ? allFields[fieldType] : null;
    }

    function getTypeaheadConfig(){
      var onlyTypeAheadKeys = _.chain(appConfig).keys(appConfig).filter(function(item){
        return item.match(/^typeahead/);
      }).value();
      return _.pick(appConfig, onlyTypeAheadKeys);
    }

    function getTypeaheadPipeline(){
      return appConfig.typeahead_query_pipeline_id;
    }

    function getTypeaheadProfile(){
      return appConfig.typeahead_query_profile_id;
    }

    function getTypeaheadRequestHandler(){
      return appConfig.typeahead_requesthandler;
    }

    function getTypeaheadField(){
      return appConfig.typeahead_fields[0];
    }

    function getFieldsToDisplay() {
      return appConfig.fields_to_display;
    }

    function getFieldLabels() {
      return appConfig.field_display_labels;
    }
  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.services.landingPage', [])
    .factory('LandingPageService', LandingPageService);

  function LandingPageService(Orwell, $window, ConfigService) {
    'ngInject';

    activate();

    var service = {
      getLandingPagesFromData: getLandingPagesFromData
    };

    return service;


    //////////////

    /**
     * This activate() is to redirect the window the first landing-page
     * in case redirect flag in appConfig
     * is `true`.
     */
    function activate() {
      var resultsObservable = Orwell.getObservable('queryResults');

      resultsObservable.addObserver(function (data) {
        var landing_pages = service.getLandingPagesFromData(data);
        if (angular.isArray(landing_pages) && ConfigService.getLandingPageRedirect()) {
          $window.location.assign(landing_pages[0]);
        }
      });
    }

    /**
     * Extracts landing pages from Fusion response data.
     */
    function getLandingPagesFromData(data) {
      return _.get(data, 'fusion.landing-pages');
    }

  }
})();
(function () {
  angular
    .module('lucidworksView.services.queryData', [
      'lucidworksView.services.config',
      'lucidworksView.services.apiBase',
      'lucidworksView.utils.queryBuilder',
      'ngOrwell'
    ])
    .config(Config)
    .provider('QueryDataService', QueryDataService);


  function Config(OrwellProvider) {
    'ngInject';
    OrwellProvider.createObservable('queryResults', {});
  }

  function QueryDataService() {

    this.$get = $get;

    /////////////

    function $get($q, $http, ConfigService, ApiBase, Orwell, QueryBuilder) {
      'ngInject';
      var queryResultsObservable = Orwell.getObservable('queryResults');
      return {
        getQueryResults: getQueryResults,
        getProfileEndpoint: getProfileEndpoint,
        getPipelineEndpoint: getPipelineEndpoint
      };

      /**
       * Make a query to the query profiles endpoint
       * @param  {object} query  Should have all the query params, like
       *                         For select?q=query&fq=blah you need to pass in an object
       *                         {'q': 'query', 'fq': 'blah'}
       * @return {Promise}       Promise that resolve with a Fusion response coming from Solr
       */
      function getQueryResults(query) {
        var deferred = $q.defer();

        var queryString = QueryBuilder.objectToURLString(query);

        var fullUrl = getQueryUrl(ConfigService.getIfQueryProfile()) + '?' + queryString;

        $http
          .get(fullUrl)
          .then(success)
          .catch(failure);

        function success(response) {
          // Set the content to populate the rest of the ui.
          queryResultsObservable.setContent(response.data);
          deferred.resolve(response.data);
        }

        function failure(err) {
          queryResultsObservable.setContent({
            numFound: 0
          });
          deferred.reject(err.data);
        }

        return deferred.promise;
      }

      /**
       * Returns the appropriate base url for an endpoint
       *
       * @param  {Boolean} isProfiles Determines which endpoint type to return;
       * @return {string}             The URL endpoint for the query without parameters.
       */
      function getQueryUrl(isProfiles) {
        if (isProfiles) {
          return getProfileEndpoint(ConfigService.getQueryProfile(), 'select');
        } else {
          return getPipelineEndpoint(ConfigService.getQueryPipeline(), 'select')
        }
      }

      function getProfileEndpoint(profile, requestHandler){
        return ApiBase.getEndpoint() + 'api/apollo/collections/' +
          ConfigService.getCollectionName() + '/query-profiles/' +
          profile + '/' + requestHandler;
      }

      function getPipelineEndpoint(pipeline, requestHandler){
        return ApiBase.getEndpoint() + 'api/apollo/query-pipelines/' +
          pipeline + '/collections/' + ConfigService.getCollectionName() +
          '/' + requestHandler;
      }

    }
  }
})();
(function () {
  angular.module('lucidworksView.services.query', ['lucidworksView.services.config',
      'lucidworksView.services.queryData'
    ])
    .config(Config)
    .constant('QUERY_OBJECT_DEFAULT', {
      q: '*',
      start: 0,
      // Do not override the return of JSON
      wt: 'json'
    })
    .provider('QueryService', QueryService);

  function Config(OrwellProvider) {
    'ngInject';
    OrwellProvider.createObservable('query', {});
  }

  function QueryService() {

    this.$get = $get;

    /////////////

    function $get($log, ConfigService, Orwell, QUERY_OBJECT_DEFAULT, QueryDataService) {
      'ngInject';
      var queryObservable = Orwell.getObservable('query'),
        queryObject = QUERY_OBJECT_DEFAULT;

      activate();

      queryObservable.addObserver(function (query) {
        QueryDataService.getQueryResults(query);

      });

      return {
        setQuery: setQuery,
        getQueryObservable: getQueryObservable,
        getQueryObject: getQueryObject
      };

      function activate() {
        queryObservable.setContent(queryObject);
      }

      function getQueryObservable() {
        return queryObservable;
      }

      function getQueryObject() {
        return queryObservable.getContent();
      }

      function setQuery(query) {
        if (ConfigService.config.query_debug) {
          $log.debug('query', query);
        }
        queryObject = _.assign({}, query, {rows: ConfigService.config.docs_per_page});
        queryObservable.setContent(queryObject);

      }

    }
  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.services.rules.filter', [])
    .provider('RulesFilterService', RulesFilterService);

  function RulesFilterService() {

    this.$get = function () {

      var facets = {
        display_type: {name: "Rules type"},
        tags: {name: "Tags"},
        enabled: {name: "Enabled"}
      };

      return {

        values: {
          query: "",
          pageSize: 10,
          pageNum: 0,

          startDate: null,
          endDate: null,

          sortByCond: null,

          facets: {}
        },

        rulesFrom: function () {
          return this.values.pageNum * this.values.pageSize;
        },

        rulesTo: function () {
          return (this.values.pageNum + 1) * this.values.pageSize -1;
        },

        filterBy: function (field, value) {
          this.values.facets[field] = {};
          this.values.facets[field][value] = true;
        },

        sortBy: function (by) {
          if (!this.values.sortByCond) {
            this.values.sortByCond = {};
            this.values.sortByCond[by] = true
          } else if (this.values.sortByCond[by]) {
            this.values.sortByCond[by] = false;
          } else {
            this.values.sortByCond = null;
          }
        },

        sortByIcon: function (by) {
          console.log("sortByIcon(" + by + ")");

          if (this.values.sortByCond === null) {
            return 'fa-sort';
          }

          if (this.values.sortByCond[by] === true) {
            return 'fa-sort-asc';
          }

          if (this.values.sortByCond[by] === false) {
            return 'fa-sort-desc';
          }

          return 'fa-sort';
        },

        hasNext: function (rulesTotal) {
          return (this.values.pageNum + 1) * this.values.pageSize < (rulesTotal || 0)
        },

        next: function () {
          this.values.pageNum += 1;
          return this.values.pageNum;
        },

        hasPrev: function () {
          return this.values.pageNum > 0;
        },

        prev: function () {
          this.values.pageNum -= 1;
          return this.values.pageNum;
        },

        toUrlString: function () {
          console.log("filter:", this.values);

          var res = "";
          res += "&q=" + (this.values.query || '*');
          res += "&facet=true";

          for (var f in facets) {
            res += "&facet.field=" + f;
          }

          res += "&start=" + this.values.pageNum * this.values.pageSize;
          res += "&rows=" + this.values.pageSize;

          var filterQuery = [];
          for (var key in this.values.facets) {
            var facet = this.values.facets[key];
            for (var name in facet) {
              if (facet[name]) {
                filterQuery.push(key + ":\"" + name + "\"");
              }
            }
          }

          if (filterQuery.length > 0) {
            res += "&fq=" + filterQuery.join("&fq=");
          }

          // : sort=<field name>+<direction>,<field name>+<direction>],...
          if (this.values.sortByCond) {
            res += "&sort=" + _.transform(this.values.sortByCond, function (result, value, key) {
                result.push(key + " " + (value ? 'asc' : 'desc'));
              }, []).join(",");
          }

          if (this.values.startDate || this.values.endDate) {
            var start = this.values.startDate ?
                  new Date(this.values.startDate.replace("T", " ")).toISOString() : "*";
            var end = this.values.endDate ?
                  new Date(this.values.endDate.replace("T", " ")).toISOString() : "*";

            res += "&fq=updatedAt:[" + start + " TO " + end + "]";
          }

          return res;
        },

        logout: function () {
          AuthService.destroySession();
        },

        reset: function () {
          this.values = {
            query: "",
            pageSize: 10,
            pageNum: 0,

            startDate: null,
            endDate: null,

            sortByCond: null,

            facets: {}
          }
        }
      }
    }
  }
}());
(function () {
  'use strict';

  angular
    .module('lucidworksView.services.rules',
      ["lucidworksView.services.config",
        "lucidworksView.services.apiBase",
        "lucidworksView.services.signals",
        "lucidworksView.services.user"])
    .provider('RulesService', RulesService);

  function RulesService() {

    this.$get = function ($http, ConfigService, ApiBase, SignalsService, UserService) {

      var appHost = ApiBase.getEndpoint();
      var solrUrl = appHost + "api/apollo/solr";
      var rulesCollection = ConfigService.config.rules.collection.trim();

      return {

        findByName: function (name, callback) {
          var url = appHost + "api/apollo/query-pipelines/" + rulesCollection + "-default/collections/" + rulesCollection + "/select?" +
            "wt=json&fl=*&json.nl=arrarr&rows=10&q=ruleName:\"" + name + "\"";

          $http.get(url).then(callback);
        },

        add: function (rule, success, error) {
          $http.post(solrUrl + '/' + rulesCollection + '/update/json/docs?commit=true', rule)
            .then(function () {
              SignalsService.postSignalData([{
                params: {
                  docId: rule.id,
                  ruleName: rule.ruleName,
                  userName: UserService.getUser().username,
                  userId: UserService.getUser().id
                },

                timestamp: new Date().toISOString(),
                type: 'rule_add'
              }]);
              success();

            }, error);
        },

        delete: function (id) {
          $http.post(solrUrl + '/' + rulesCollection + '/update?commit=true', {'delete': {id: id}})
            .then(function (response) {
              SignalsService.postSignalData([{
                params: {
                  docId: id,
                  userName: UserService.getUser().username,
                  userId: UserService.getUser().id
                },
                timestamp: new Date().toISOString(),
                type: 'rule_delete'
              }]);

              console.log("Rule '" + id + "' deleted!");
            });
        },

        update: function (id, rule) {
          $http.post(solrUrl + '/' + rulesCollection + '/update/json/docs?commit=true', rule)
            .then(function (response) {
