(function () {
  'use strict';

  angular
    .module('lucidworksView.services.signals', ['lucidworksView.services.apiBase',
      'lucidworksView.services.config'
    ])
    .factory('SignalsService', SignalsService);

  function SignalsService(ApiBase, ConfigService, $http, $q, QueryService) {
    'ngInject';
    var service = {
      postClickSignal: postClickSignal,
      postSignalData: postSignalData,
      getSignalsDocumentId: getSignalsDocumentId
    };

    return service;

    /**
     * Helper method to document click events.
     * @param  {string} docId      The document id
     * @param  {object} options     An object containing parameter overrides and options.
     *   - The object can be any parameter which will be passed through, including parameters.
     *     Ex: {type: 'custom', params: {filterQueries: ['something']}}
     * @return {promise}
     */
    function postClickSignal(docId, options) {
      var date = new Date(),
        data = {
          params: {
            docId: docId,
            query: QueryService.getQueryObject().q
          },
          pipeline: ConfigService.config.signals_pipeline,
          timestamp: date.toISOString(),
          type: ConfigService.config.signal_type
        };

      _.defaultsDeep(data, options);


      return postSignalData([data]);
    }

    //Use if you want to post a raw signal where you have control of every signal entry.  See postClickSignal for an example data structure
    function postSignalData(data) {
      var deferred = $q.defer();
      $http
        .post(ApiBase.getEndpoint() + 'api/apollo/signals/' + ConfigService.config.collection,
          data)
        .then(success)
        .catch(failure);

      function success(response) {
        deferred.resolve(response.data);
      }

      function failure(err) {
        deferred.reject(err.data);
      }

      return deferred.promise;
    }

    /**
     * Given a document return a signals document ID value.
     * @param  {object} doc The document
     * @return {string|Number}     The document ID value
     */
    function getSignalsDocumentId(doc) {
      var documentIdField = ConfigService.config.signals_document_id;
      if (doc.hasOwnProperty(documentIdField)) {
        return doc[documentIdField];
      }
      else {
        return null;
      }

    }
  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.services.simulation', ["lucidworksView.services.config", "lucidworksView.services.apiBase"])
    .provider('SimulationService', SimulationService);

  function SimulationService() {

    this.$get = function ($http, $log, ConfigService, ApiBase) {

      var appHost = ApiBase.getEndpoint();
      var collection = ConfigService.config.collection.trim();
      var appName = '&appName=' + collection + 'AdvUI';

      var config = ConfigService.config.simulation;

      function escape(str) {
        return  encodeURI(str).replace(/&/g, '%26').replace(/\//g, '%2F').replace(/#/g, '%23');
      }

      function geo(lw) {
        if (lw.geo_enabled && lw.coordinates.length > 0) {
          return '&sfield=' + lw.geofield + '&pt=' + lw.coordinates
            + '&d=' + lw.distance + '&fq={!bbox}&queryOpts=spatial&fl=distance:product(geodist(),0.621)';
        }

        return '';
      }

      function sort(lw) {
        if (lw.sort_by == lw.head_field) {
          return '&sort=' + lw.head_field + ' ' + lw.sort_order;
        } else if (lw.sort_by == "distance" && lw.coordinates.length > 0) {
          return '&sort=geodist()' + ' ' + lw.sort_order; //Sort by distance
        } else {
          return ''; //Sort by relevannce
        }
      }

      function query(q) {
        return '?wt=json' +
               '&json.nl=arrarr' +
               '&debug=true' +
               '&fl=score,explain:[explain style=nl]' +
               '&q=' + escape(q || '*:*');
      }

      function mm(lw) {
        return lw.mm == 'default' ? '' : '&mm=' + lw.mm;
      }

      function fq(fqs) {
        var res = '';
        for (var i = 0; i < fqs.length; i++) {
          res += '&fq=' + escape(fqs[i])
        }

        return res;
      }


      return {

        search: function (lw, q, fqs, within, start, rules_exclude, incexlStr, success, error) {
          var url = appHost + 'api/apollo/query-pipelines/' + lw.pipename + '/' + 'collections/' + collection
            + '/' + lw.requestHandler + query(q) + within + fq(fqs) + '&fl=' + lw.fl + '&start=' + start + sort(lw) + geo(lw)
            + rules_exclude + incexlStr
            + mm(lw) + appName + lw.addl_params;

          $http.get(url)
            .success(success)
            .error(error);
        },

        spellCheck: function (q, success, error) {
          if (!config.spellCheck) {
            $log.error("spellCheck was called but wasn't configured!");
          }

          var url = appHost + 'api/apollo/solr/' + collection + '/' + config.spellCheck.requestHandler
            + '?wt=json&spellcheck.q=' + q + '&spellcheck.dictionary=' + config.spellCheck.dictionary;

          $http.get(url).success(success).error(error)
        }
      };
    };
  }
})();


