(function () {
  'use strict';
  angular
    .module('lucidworksView.components.banner', [])
    .directive('banner', banner);

  function banner() {
    'ngInject';
    return {
      controller: Controller,
      templateUrl: 'assets/components/banner/banner.html',
      controllerAs: 'bd',
      bindToController: true,
      scope: true
    };
  }

  function Controller($scope, Orwell) {
    'ngInject';
    var bd = this;
    bd.banners = false;

    var resultsObservable = Orwell.getObservable('queryResults');

    resultsObservable.addObserver(function (data) {
      var banners = _.get(data, 'fusion.banner');
      if (!banners) {
        var rules = _.get(data, 'fusion.applicable_rules');
        if (!rules) {
          bd.banners = false;
          return false;
        }

        var excludedRules = _.get(data, 'fusion.excluded_rules') || [];

        banners = _.chain(rules)
          .filter(function (r) {
            var type = r.display_type;
            if ((type == "Banner" || _.isArray(type) && type.indexOf("Banner") != -1)
                  && excludedRules.indexOf(r.id) == -1) {
              return true;
            }

            if ((r.type == "response_value" && r.keys.indexOf("banner") != -1)
                  && excludedRules.indexOf(r.id) == -1) {
              return true;
            }

            return false;
          })
          .flatMap(function (rule) {
            return rule.values
          })
          .value();
      }

      if (banners.length > 0) {
        bd.banners = banners;
      } else {
        bd.banners = false;
      }
    });
  }

})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.documentList', ['lucidworksView.services.config',
      'ngOrwell', 'lucidworksView.services.landingPage'
    ])
    .directive('documentList', documentList);

  function documentList() {
    'ngInject';
    return {
      restrict: 'EA',
      templateUrl: 'assets/components/documentList/documentList.html',
      controller: Controller,
      controllerAs: 'vm',
      bindToController: {},
      scope: true,
      replace: true
    };

  }

  function Controller($sce, $log, $anchorScroll, Orwell) {
    'ngInject';
    var vm = this;
    vm.docs = [];
    vm.highlighting = {};
    vm.getDocType = getDocType;
    vm.groupedResults = false;
    vm.toggleGroupedResults = toggleGroupedResults;
    vm.showGroupedResults = {};

    activate();

    ////////

    function activate() {
      var resultsObservable = Orwell.getObservable('queryResults');
      resultsObservable.addObserver(function (data) {
        vm.docs = parseDocuments(data);
        vm.highlighting = parseHighlighting(data);
        vm.getDoctype = getDocType;
        $anchorScroll('topOfMainContent');
      });
    }

    /**
     * Get the document type for the document.
     * @param  {object} doc Document object
     * @return {string}     Type of document
     */
    function getDocType(doc){
      // Change to your collection datasource type name
      // if(doc['_lw_data_source_s'] === 'MyDatasource-default'){
      //   return doc['_lw_data_source_s'];
      // }
      return doc['_lw_data_source_type_s'];
    }

    /**
     * Decorates the document object before sending to the document directive.
     * @param  {object} doc Document object
     * @return {object}     Document object
     */

    function isNotGrouped(data){
      return _.has(data, 'response');
    }
    function isGrouped(data){
      return _.has(data, 'grouped');
    }
    /**
     * Get the documents from
     * @param  {object} data The result data.
     * @return {array}       The documents returned
     */
    function parseDocuments(data){
      var docs = [];
      if (isNotGrouped(data)) {
        docs = data.response.docs;
      }
      else if(isGrouped(data)){
        vm.groupedResults = data.grouped;
        parseGrouping(vm.groupedResults);
      }
      return docs;
    }


    function toggleGroupedResults(toggle){
      vm.showGroupedResults[toggle] = !vm.showGroupedResults[toggle];
    }

    function parseGrouping(results){
      _.each(results, function(item){
        _.each(item.groups, function(group){
          if(_.has(group, 'groupValue') && group.groupValue !== null){
            vm.showGroupedResults[group.groupValue] = false;
          }
          else{
            vm.showGroupedResults['noGroupedValue'] = true;
          };
        });
      });
    }

    /**
     * Get highlighting from a document.
     * @param  {object} data The result data.
     * @return {object}      The highlighting results.
     */
    function parseHighlighting(data) {
      if (data.hasOwnProperty('highlighting')){
        _.each(data.highlighting, function(value, key){
          var vals = {};
          if (value) {
            _.each(Object.keys(value), function (key) {
              $log.debug('highlight', value);
              var val = value[key];
              _.each(val, function(high){
                vals[key] = $sce.trustAsHtml(high);
              });
            });
            vm.highlighting[key] = vals;
          }
        });
      }
      else{
        vm.highlighting = {};
      }
      return vm.highlighting;
    }
  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.dragAndDropFile', [])
    .directive('dragAndDropFile', dragAndDropFile);

    function dragAndDropFile () {
      'ngInject';
      return {
        restrict: 'EA',
        scope: {ngModel: '=', rightFileType: '='},
        link: function (scope, element, attrs) {
          var processDragOverOrEnter;

          processDragOverOrEnter = function (event) {
            if (event !== null) {
              event.preventDefault();
            }
            event.dataTransfer.effectAllowed = 'copy';
            return false;
          };
          element.bind('dragover', processDragOverOrEnter);
          element.bind('dragenter', processDragOverOrEnter);
          element.bind('drop', handleDropEvent);

          function insertText(loadedFile) {
            console.log("insertText");
            console.log(scope.ngModel);
            var content = loadedFile.target.result.split(/,|\n/);
            console.log(content);
            var modelContent = scope.ngModel || [];
            var areaContent = modelContent.concat(content);
            console.log(areaContent);
            scope.ngModel = areaContent;
            scope.$apply();
          }

          function handleDropEvent(event) {

            if (event !== null) {
              event.preventDefault();
            }
            var reader = new FileReader();
            reader.onload = insertText;
            reader.readAsText(event.dataTransfer.files[0]);
          }
        }
      };
    }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.facetField', [
      'lucidworksView.services.config',
      'foundation.core',
      'lucidworksView.utils.queryBuilder',
      'angular-humanize'
    ])
    .config(Config);

  function Config(QueryBuilderProvider){
    'ngInject';
    // Register transformers because facet fields can have funky URL syntax.
    QueryBuilderProvider.registerTransformer('keyValue', 'fq:field', fqFieldkeyValueTransformer);
    QueryBuilderProvider.registerTransformer('preEncodeWrapper', 'fq:field', fqFieldPreEncodeWrapper);
    QueryBuilderProvider.registerTransformer('encode', 'fq:field', fqFieldEncode);
    QueryBuilderProvider.registerTransformer('wrapper', 'fq:field', fqFieldWrapper);

    // TODO properly implement transformer for localParens
    QueryBuilderProvider.registerTransformer('join', 'localParens', localParenJoinTransformer);
    QueryBuilderProvider.registerTransformer('wrapper', 'localParens', localParenWrapperTransformer);

    /**
     * Transformers.
     *
     * These will transform the output of the query, when the query is created.
     */

    function fqFieldkeyValueTransformer(key, value) {
      //var escapedKey = QueryBuilderProvider.escapeSpecialChars(key);
      // at least we need something smarter then simple escaping because field name can contain '-' (dash) for example
      // and simple escaping make this field name invalid.
      return QueryBuilderProvider.keyValueString(key, value, ':');
    }

    function fqFieldPreEncodeWrapper(data){
      return '"'+data+'"';
    }

    function fqFieldEncode(data){
      return QueryBuilderProvider.encodeURIComponentPlus(data);
    }

    function fqFieldWrapper(data){
      return '('+data+')';
    }

    function localParenJoinTransformer(str, values) {
      return QueryBuilderProvider.arrayJoinString(str, values, ' ');
    }

    function localParenWrapperTransformer(data) {
      return '{!' + data + '}';
    }
  }

})();
(function() {
  'use strict';

  angular
    .module('lucidworksView.components.facetField')
    .directive('facetField', facetField);

  function facetField() {
    'ngInject';
    return {
      restrict: 'EA',
      templateUrl: 'assets/components/facetField/facetField.html',
      scope: true,
      controller: Controller,
      controllerAs: 'vm',
      bindToController: {
        facetName: '@facetName',
        facetLabel: '@facetLabel',
        facetAutoOpen: '@facetAutoOpen'
      }
    };
  }

  function Controller(ConfigService, QueryService, URLService, QueryDataService, Orwell, FoundationApi, $filter) {
    'ngInject';
    var vm = this;
    vm.facetCounts = [];
    vm.toggleFacet = toggleFacet;
    vm.toggleMore = toggleMore;
    vm.getLimitAmount = getLimitAmount;
    vm.more = false;
    var resultsObservable = Orwell.getObservable('queryResults');

    activate();

    //////////////

    /**
     * Activate the controller.
     */
    function activate() {

      // Add observer to update data when we get results back.
      resultsObservable.addObserver(parseFacets);
      // initialize the facets.
      parseFacets(resultsObservable.getContent());
    }

    /**
     * Toggles the more button for the facet.
     */
    function toggleMore(){
      vm.more = !vm.more;
    }

   /**
    * Gets the amount to limit by
    * @return {integer|undefined} The amount to return or undefined.
    */
    function getLimitAmount(){
      if(vm.more){
        return undefined;
      }
      return 5;
    }

    /**
     * Parses the facets from an observable into an array of facets.
     * @param  {object} data The data from the observable.
     */
    function parseFacets(data){
      // Exit early if there are no facets in the response.
      if (!data.hasOwnProperty('facet_counts')) return;

      // Determine if facet exists.
      var facetFields = data.facet_counts.facet_fields;
      if (facetFields.hasOwnProperty(vm.facetName)) {
        // Transform an array of values in format [aaaa, 1234,bbbb,2345] into an array of objects.
        vm.facetCounts = arrayToObjectArray(facetFields[vm.facetName]);
      }

      // Set inital active state
      var active = true;
      // If we have autoOpen set active to this state.
      if (angular.isDefined(vm.facetAutoOpen) && vm.facetAutoOpen === 'false') {
        active = false;
      }
      vm.active = active;
    }

    /**
     * Turn a flat array into an object array.
     *
     * Transforms an array of values in format ['aaaa', 1234,'bbbb',2345] into
     * an array of objects.
     * [{title:'aaaa', amount:1234, hash: zf-hs-njkfhdsle},
     *  {title:'bbbb', amount:2345, hash: zf-hs-jkewrkljn}]
     *
     * @param  {array} arr The array to transform
     * @return {array}     An array of objects
     */
    function arrayToObjectArray(arr) {
      return _.transform(arr, function (result, value, index) {
        if (index % 2 === 1) {
          result[result.length - 1] = {
            title: result[result.length - 1],
            amount: value,
            amountFormatted: $filter('humanizeNumberFormat')(value, 0),
            hash: FoundationApi.generateUuid(),
            active: isFacetActive(vm.facetName, result[result.length - 1])
          };
        } else {
          result.push(value);
        }
      });
    }

    /**
     * Toggles a facet on or off depending on it's current state.
     * @param  {object} facet The facet object
     */
    function toggleFacet(facet) {
      var key = vm.facetName;
      var query = QueryService.getQueryObject();

      // CASE: fq exists.
      if(!query.hasOwnProperty('fq')){
        query = addQueryFacet(query, key, facet.title);
      } else {
        // Remove the key object from the query.
        // We will re-add later if we need to.
        var keyArr = _.remove(query.fq, {key: key, transformer:'fq:field'});

        // CASE: facet key exists in query.
        if(keyArr.length > 0) {
          var keyObj = keyArr[0];
          var removed = _.remove(keyObj.values, function(value){return value === facet.title;});
          // CASE: value didn't previously exist add to values.
          if(removed.length === 0){
            if(!keyObj.hasOwnProperty('values')){
              keyObj.values = [];
            }
            keyObj.values.push(facet.title);
          }
          // CASE: there are still values in facet attach keyobject back to query.
          if(keyObj.values.length > 0){
            query.fq.push(keyObj);
          }
          // Delete 'fq' if it is now empty.
          if(query.fq.length === 0){
            delete query.fq;
          }
        } else { // CASE: Facet key doesn't exist ADD key AND VALUE.
          query = addQueryFacet(query, key, facet.title);
        }

      }
      // Set the query and trigger the refresh.
      updateFacetQuery(query);
    }

    /**
     * Sets the facet query and sets start row to beginning.
     * @param  {object} query The query object.
     */
    function updateFacetQuery(query){
      query.start = 0;
      URLService.setQuery(query);
    }

    /**
     * Determine if a facet is currently active.
     * @param  {string}  key   The key for the facet
     * @param  {object}  value the facet
     * @return {Boolean}       [description]
     */
    function isFacetActive(key, value){
      var query = QueryService.getQueryObject();
      if(!query.hasOwnProperty('fq')){
        return false;
      }
      var keyObj = _.find(query.fq, {key: key, transformer: 'fq:field'});
      if(_.isEmpty(keyObj)){
        return false;
      }
      if(_.isEmpty(_.find(keyObj.values, function(data){return data === value;}))){
        return false;
      }
      return true;
    }

    function addQueryFacet(query, key, title){
      if(!query.hasOwnProperty('fq')){
        query.fq = [];
      }
      var keyObj = {
        key: key,
        values: [title],
        transformer: 'fq:field'
      };
      query.fq.push(keyObj);
      return query;
    }

  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.facetList', ['lucidworksView.services.config'])
    .directive('facetList', facetList);

  function facetList() {
    'ngInject';

    return {
      restrict: 'EA',
      templateUrl: 'assets/components/facetList/facetList.html',
      scope: true,
      controller: Controller,
      controllerAs: 'vm',
      bindToController: {}
    };
  }

  function Controller(ConfigService, Orwell) {
    'ngInject';
    var vm = this;
    var resultsObservable = Orwell.getObservable('queryResults');
    vm.facets = [];
    vm.facetNames = {};

    resultsObservable.addObserver(function (data) {

      if (!data.hasOwnProperty('facet_counts')) {
        return; // Exit early if there are no facets in the response.
      }

      _.forEach(data.facet_counts, function (resultFacets, facetType){
        if(_.isEmpty(resultFacets)){
          return; // Return early if no facets exists for type.
        }

        var facetFields = Object.keys(resultFacets);
        if (_.isEqual(vm.facetNames[facetType], facetFields)) {
          return; // Keep a list of facet names and only reflow facets based on changes to this list.
        }

        vm.facetNames[facetType] = facetFields;

        vm.facets = _.concat(vm.facets, facetFields.map(function (value) {
          return {
            name: value,
            type: facetType,
            autoOpen: true,
            label: ConfigService.getFieldLabels()[value] || value
          };
        }));
      });
    });

  }
})();
(function () {
  angular.module('lucidworksView.components.field', ['lucidworksView.services.config',
      'lucidworksView.utils.docs'
    ])
    .directive('field', fieldItem);


  function fieldItem() {
    'ngInject';
    return {
      restrict: 'EA',
      templateUrl: 'assets/components/field/field.html',
      scope: true,
      controller: Controller,
      controllerAs: 'fc',
      bindToController: {
        value: '=',
        highlight: '=',
        hkey: '@', //The Highlight key, used to look up the proper highlighting snippet by name.
        maxlength: '='
      },
      replace: true
    };
  }

  function Controller() {
    'ngInject';
    var fc = this;

    activate();

    ///////////

    function activate() {
      fc.value = processField(fc.value, fc.hkey, fc.highlight, fc.maxlength);
    }

    function processField(field, highlightKey, highlight, maxlength) {
      var result = field;
      var hasHighlight = false;

      if (highlight && Object.keys(highlight).length > 0) {
        if (highlight[highlightKey]) {
          result = highlight[highlightKey];
        }
      }
      // Only shorten if not highlighting, since highlighing in solr can control
      // snippet size there and b/c we have a Trusted object and not a plain
      // old string.

      // If field is multivalued, join the items before trimming
      result = _.isArray(result)?_.join(result, ' '):result;

      if (hasHighlight === false && result && result.length > maxlength) {
        // Trim it, ideally on whitespace
        var idx = result.lastIndexOf(' ', maxlength);
        if (idx !== -1) {
          result = result.substring(0, idx);
        } else {
          // We can't find simple whitespace, so just trim arbitrarily
          result = result.substring(0, maxlength);
        }
        result += '...';
      }
      return result;
    }
  }
})();
