(function () {
  'use strict';

  angular
    .module('lucidworksView.components.paginate')
    .directive('paginate', paginate);

  function paginate() {
    'ngInject';
    var directive = {
      restrict: 'EA',
      templateUrl: 'assets/components/paginate/paginate.html',
      controller: Controller,
      controllerAs: 'vm',
      bindToController: true,
      scope: true
    };

    return directive;

  }

  function Controller(Orwell, PaginateService, URLService, $filter) {
    'ngInject';
    var vm = this;
    vm.page = 0;
    vm.totalPages = 0;
    vm.getNormalizedPage = getNormalizedPage;
    vm.getNormalizedPageFormatted = getNormalizedPageFormatted;
    vm.getLastPage = getLastPage;
    vm.gotoNextPage = gotoNextPage;
    vm.gotoPreviousPage = gotoPreviousPage;
    vm.showState = 'next';

    activate();

    /////////////

    function activate() {
      var resultsObservable = Orwell.getObservable('queryResults');

      resultsObservable.addObserver(function (data) {
        if (data.hasOwnProperty('response')) {
          vm.page = PaginateService.getCurrentPage();
          vm.totalPages = PaginateService.getTotalPages();
          vm.totalPagesFormatted = $filter('humanizeNumberFormat')(vm.totalPages, 0);
          vm.showState =  pickPaginatorType();
        } else {
          vm.page = 0;
          vm.totalPages = 0;
        }
      });
    }

    function pickPaginatorType(){
      if(vm.totalPages < 1) return 'neither';
      if(vm.page === 0 && vm.totalPages > 1) return 'next';
      if(vm.page === vm.getLastPage() && vm.page > 0) return 'previous';
      if(vm.page !== vm.getLastPage()) return 'both';
    }

    /**
     * Get the page number (normalized for viewers from a 1 base)
     * @return {integer} normalized page number
     */
    function getNormalizedPage() {
      return vm.page + 1;
    }

    function getNormalizedPageFormatted(){
      return $filter('humanizeNumberFormat')(getNormalizedPage(), 0);
    }

    /**
     * Get the last page (0 start based)
     * @return {integer} The last page from 0 base.
     */
    function getLastPage() {
      return vm.totalPages - 1;
    }

    /**
     * Updates the query parameters to the next page.
     */
    function gotoNextPage() {
      gotoPage(PaginateService.getCurrentPage() + 1);
    }

    /**
     * Updates the query parameters to the previous page.
     */
    function gotoPreviousPage() {
      gotoPage(PaginateService.getCurrentPage() - 1);
    }

    /**
     * Actually updates the page to go to.
     * @param  {integer} page The page to do to
     */
    function gotoPage(page) {
      if (page < 0) return;
      if (page > PaginateService.getTotalPages()) return;
      if (page === PaginateService.getCurrentPage()) return;
      // This will change the query and cause the interface to make an http call.
      URLService.setQuery({
        start: PaginateService.pageToStartRow(page)
      });
    }

  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.paginate')
    .factory('PaginateService', PaginateService);


  function PaginateService(Orwell) {
    'ngInject';
    var queryObservable, resultsObservable,
      service = {
        pageToStartRow: pageToStartRow,
        getRowsPerPage: getRowsPerPage,
        getCurrentPage: getCurrentPage,
        getTotalPages: getTotalPages
      };

    activate();

    return service;

    ////////////////

    /**
     * Activate the service.
     */
    function activate(){
      queryObservable = Orwell.getObservable('query');
      resultsObservable = Orwell.getObservable('queryResults');
    }

    /**
     * Turn a page into a start row.
     * @param  {integer} page        The page index
     * @return {integer}             The start row
     */
    function pageToStartRow(page) {
      return page * getRowsPerPage();
    }

    /**
     * Get the number of Rows Per Page
     *
     * @return {integer} Rows per page
     */
    function getRowsPerPage() {
      var query = queryObservable.getContent();
      return query.rows;
    }

    /**
     * Get the current page
     * @return {integer} The page number
     */
    function getCurrentPage() {
      return getPage(getCurrentStartRow());
    }

    /**
     * Get the total number of pages for the query.
     * @return {integer} The number of pages
     */
    function getTotalPages() {
      if (getRowsPerPage() === 0) return 0;
      // total pages = CEIL(ALL ROWS / ROWS PER PAGE)
      return (getTotalResultRows() > 0) ? (Math.ceil(getTotalResultRows() /
        getRowsPerPage())) : 0;
    }

    /////////////////////
    // Private Methods //
    /////////////////////

    /**
     * Get the current start row.
     * @return {integer} The start row
     */
    function getCurrentStartRow() {
      var query = queryObservable.getContent();
      return query.start;
    }

    /**
     * Get the total number of results rows for the current query.
     * @return {integer} Number of result rows.
     */
    function getTotalResultRows() {
      var results = resultsObservable.getContent();
      if (results.hasOwnProperty('response')) {
        return results.response.numFound;
      }
      return 0;
    }

    /**
     * Get a page given a start row.
     * @param  {integer} startRow The row to start the page from
     * @return {integer}          The page number
     */
    function getPage(startRow){
      if (getRowsPerPage() === 0 || startRow === 0) return 0;
      return Math.ceil(startRow / getRowsPerPage());
    }

  }
})();
