(function () {
  'use strict';

  angular
    .module('lucidworksView.components.searchbox')
    .factory('SearchBoxDataService', SearchBoxDataService);

  function SearchBoxDataService($http, $q, ConfigService, ApiBase, QueryBuilder, QueryDataService){
    'ngInject';

    return {
      getTypeaheadResults: getTypeaheadResults
    };

    ////////////

    function getTypeaheadResults(query){
      var deferred = $q.defer();

      var queryString = QueryBuilder.objectToURLString(query);

      var fullUrl = getQueryUrl(ConfigService.getIfQueryProfile()) + '?' + queryString;

      $http
        .get(fullUrl)
        .then(success)
        .catch(failure);

      function success(response) {
        deferred.resolve(response.data);
      }

      function failure(err) {
        deferred.reject(err.data);
      }

      return deferred.promise;
    }

    /**
     * Private function
     */
    function getQueryUrl(isProfile){
      var requestHandler = ConfigService.getTypeaheadRequestHandler();

      var profileUrl = QueryDataService.getProfileEndpoint(ConfigService.getTypeaheadProfile(), requestHandler);

      var pipelineUrl = QueryDataService.getPipelineEndpoint(ConfigService.getTypeaheadPipeline(), requestHandler);

      return isProfile?profileUrl:pipelineUrl;
    }

  }
})();
(function () {
  'use strict';

  angular
    .module('lucidworksView.components.searchbox')
    .directive('searchBox', searchbox);

  function searchbox() {
    'ngInject';
    return {
      restrict: 'EA',
      controller: Controller,
      templateUrl: 'assets/components/searchBox/searchBox.html',
      scope: true,
      controllerAs: 'ta',
      bindToController: {
        query: '='
      },
      require: '^form'
    };
  }

  function Controller($scope, $q, ConfigService, QueryService,
    SearchBoxDataService) {
    'ngInject';
    var ta = this;
    ta.typeaheadField = ConfigService.getTypeaheadField();
    ta.doTypeaheadSearch = doTypeaheadSearch;
    ta.selectedSomething = selectedSomething;
    ta.updateSearchQuery = updateSearchQuery;
    ta.initialValue = _.isArray(ta.query)?ta.query[0]:ta.query;

    function selectedSomething(object) {
      if (object) {
        var newValue = object.originalObject[ta.typeaheadField];
        ta.query = _.isArray(newValue)?newValue[0]:newValue;
      }
    }

    function updateSearchQuery(inputString) {
      ta.query = inputString;
    }

    function doTypeaheadSearch(userInputString, timeoutPromise) {
      var deferred = $q.defer();

      SearchBoxDataService
        .getTypeaheadResults({q: ta.query, wt: 'json'})
        .then(function (resp) {
          if(resp.hasOwnProperty('response')) {
            var objectToResolve = {
              data: resp.response.docs
            };
            deferred.resolve(objectToResolve);
          } else {
            deferred.reject('No response docs');
          }
        })
        .catch(function (error) {
          timeoutPromise.reject(error);
        });

      return deferred.promise;
    }


  }

})();
(function() {
  'use strict';

  angular
    .module('lucidworksView.components.sort', [])
    .directive('sort', sort);

  function sort() {
    'ngInject';
    var directive = {
      restrict: 'E',
      templateUrl: 'assets/components/sort/sort.html',
      scope: true,
      controller: Controller,
      controllerAs: 'vm',
      bindToController: {}
    };

    return directive;


  }

  function Controller($scope, ConfigService, QueryService, URLService) {
    'ngInject';
    var vm = this;
    vm.switchSort = switchSort;

    activate();

    /////////////

    function activate() {
      createSortList();

      $scope.$watch('vm.selectedSort', handleSelectedSortChange);
    }

    function createSortList(){
      var sortOptions = [{label:'default sort', type:'default', order:'', active: true}];
      _.forEach(ConfigService.config.sort_fields, function(value){
        sortOptions.push({label: value, type: 'text', order: 'asc', active: false});
        sortOptions.push({label: value, type: 'text', order: 'desc', active: false});
      });
      vm.sortOptions = sortOptions;
      vm.selectedSort = vm.sortOptions[0];
    }

    function handleSelectedSortChange(newValue, oldValue){
      if(newValue === oldValue) return;

      switchSort(newValue);
    }

    function switchSort(sort){
      var query = QueryService.getQueryObject();
      switch(sort.type) {
      case 'text':
        if(angular.isUndefined(query.sort)){
          query.sort = sort.label+'%20'+sort.order;
          URLService.setQuery(query);
        }
        break;
      default:
        delete query.sort;
        URLService.setQuery(query);
      }
    }
  }
})();
(function () {
  angular.module('lucidworksView.components.document', ['lucidworksView.services.config',
      'lucidworksView.utils.docs', 'lucidworksView.services.signals'
    ])
    .directive('documentDefault', documentDefault);


  function documentDefault() {
    'ngInject';
    return {
      templateUrl: 'assets/components/document/document_default/document_default.html',
      scope: true,
      controller: Controller,
      controllerAs: 'vm',
      bindToController: {
        doc: '=',
        highlight: '='
      },
      replace: true
    };
  }

  function Controller($log, $scope, DocsHelper, ConfigService, SignalsService) {
    'ngInject';
    var vm = this;
    vm.postSignal = SignalsService.postClickSignal;

    activate();

    ///////////

    function activate() {
      vm.doc = processDocument(DocsHelper.concatMultivaluedFields(vm.doc));
    }

    /**
     * Processes a document prepares fields from the config for display.
     * @param  {object} doc A single document record
     * @return {object}     The document record with processed properties.
     */
    function processDocument(doc) {

      // Populate the additional fields to display

      // Get fields from config service.
      var fieldsToDisplay = ConfigService.getFieldsToDisplay();
      // Parse any wildcards in the config.
      fieldsToDisplay = DocsHelper.parseWildcards(fieldsToDisplay, doc);
      // turn fields to display into a list of params.
      doc.fieldsToDisplay = DocsHelper.populateFieldLabels(
        DocsHelper.selectFields(doc, fieldsToDisplay),
        ConfigService.getFieldLabels()
      );

      doc.lw_head = getField('head', doc) ?
        getField('head', doc) : 'Title Field Not Found';

      doc.lw_subhead = getField('subhead', doc);

      doc.lw_description = getField('description', doc);

      doc.lw_image = getField('image', doc);

      doc.lw_url = getField('head_url', doc);

      return doc;
    }

    /**
     * Given a field type get the actual field value.
     * @param  {String} fieldType The Field type.
     * @param  {object} doc       The document object
     * @return {String|null}       The field value.
     */
    function getField(fieldType, doc) {
      var fieldName = ConfigService.getFields.get(fieldType);
      if (doc.hasOwnProperty(fieldName)) {
        return doc[fieldName];
      }
      return null;
    }

  }
})();
